{
  "permissions": {
    "jobs": {
      "create": true,
      "read": true,
      "update": true,
      "delete": false
    },
    "candidates": {
      "create": true,
      "read": true,
      "update": true,
      "delete": false
    }
  }
}

{
  "title": "Senior Software Engineer",
  "description": "We are looking for a senior software engineer to join our team",
  "department": "Engineering",
  "location": {
    "type": "hybrid",
    "city": "San Francisco",
    "state": "CA",
    "country": "USA"
  },
  "employment": {
    "type": "full-time",
    "level": "senior"
  },
  "salary": {
    "min": 120000,
    "max": 180000,
    "currency": "USD",
    "period": "annual"
  },
  "requirements": {
    "experience": "5+ years",
    "education": "Bachelor's degree in Computer Science or related field",
    "skills": ["JavaScript", "React", "Node.js", "TypeScript", "MongoDB"]
  },
  "benefits": ["Health insurance", "401k", "Flexible hours", "Remote work"],
  "hiringManager": "{{user_id}}",
  "urgency": "medium"
}

{
  "candidateIds": ["{{candidate_id}}"],
  "dueDate": "2024-12-25T23:59:59.000Z",
  "instructions": "Please complete this assessment by the due date",
  "sendNotification": true
}
